import React, { Component } from 'react'
import './UserCreate.css'
import Header from '../../components/Header/Header'

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
// import InputGroup from 'react-bootstrap/InputGroup'
import { Col  } from 'react-bootstrap';

const HeaderProps = {
    title: "Cadastrar pessoa"
}



class UserCreate extends Component {

    constructor(){
        super()
        this.state = {
            name: 'wagner alves',
            email: 'wagner@gmail.com'
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        let target = event.target
        let index = target.name
        if(!index){

        }
        this.setState({
            [index]: target.value
        });
      }

    handleSubmit(event) {
        alert('Um nome foi enviado: ' + this.state.name);
        event.preventDefault();
      }

    render() {
        return (
            <div>
                <Header {...HeaderProps} />
                <Form className="form-userCreate">
                    <Form.Row>
                        <Form.Group as={Col} md="4" controlId="validationCustom01">
                            <Form.Label>First name</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                placeholder="First name"
                                name="name"
                                value={this.state.name}
                                onChange={this.handleChange}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                    <Form.Group controlId="formBasicEmail">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control 
                                type="email" 
                                placeholder="Enter email" 
                                name="email"
                                value={this.state.email}
                                onChange={this.handleChange}
                                />
                            <Form.Text className="text">
                            {this.state.email} - {this.state.name}
                            </Form.Text>
                        </Form.Group>
                    </Form.Row>
                    {/* <input className="button-submit-UserCreate" type="button" value="Confirmar" /> */}
                    <Button onClick={this.handleSubmit} className="button-submit-UserCreate" variant="primary">Confirm</Button>
                    <Button className="button-cancel-UserCreate" variant="danger"><a href="/">Cancel</a></Button>
                </Form>
            </div>
        )
    }
}

export default UserCreate;
