import React, { Component } from 'react'
import './UserView.jsx'

import Table from 'react-bootstrap/Table'
import Header from '../../components/Header/Header'
import axios from 'axios'

const HeaderProps = {
    title: "Listagem de pessoas"
}

const GetUsers = {}
const Users = []


class UserView extends Component {

    

    async componentDidMount() {
        axios.get('http://localhost:3333/usuarios')
            .then(function (res) {
                // response.map(item => [console.log(item.id)])
                GetUsers.data=res.data
                // console.log(GetUsers.data[0].id)   
                const TamanhoObjetoUsuarios = Object.values(GetUsers)[0].length
                for(let index = 0; index < TamanhoObjetoUsuarios; index++) {
                    console.log(Object.values(GetUsers)[0][index].id)
                }
            }) 
    }

    // Fortable() {
    //     for(let index = 0; index < TamanhoObjetoUsuarios; index++) {
    //         `<tr key={item}>
    //         <td>{item.name}</td>
    //         <td>{item.age}</td>
    //     </tr>`
    //     }
    // }

    render() {
        return (
            <div>
                <div>
                    <Header  {...HeaderProps} />
                </div>
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Idade</th>
                        </tr>
                    </thead>
                    <tbody>
                        {/* {users.map((item) => (
                            <tr key={item}>
                                <td>{item.name}</td>
                                <td>{item.age}</td>
                            </tr>
                        ))} */}
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default UserView;
