import React, { Component } from 'react'
import './UserView.jsx'

import Table from 'react-bootstrap/Table'
import Header from '../../components/Header/Header'
import axios from 'axios'

const bd = () => {
    axios.get('http://localhost:3333/usuarios', (req, res)=>{
        console.log(res.data)
    })
}

const HeaderProps = {
    title: "Listagem de pessoas"
}

const abc = [{
    name: "wagner",
    age: 23
}, {
    name: "Alves",
    age: 25
}
]

bd()

class UserView extends Component {
    render() {
        return (
            <div>
                <div>
                    <Header {...HeaderProps} />
                </div>
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Idade</th>
                        </tr>
                    </thead>
                    <tbody>
                        {abc.map((item, index) => (
                            <tr key={index}>
                                <td>{item.name}</td>
                                <td>{item.age}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default UserView;
