import React, { Component } from 'react'
import './UserView.jsx'

import Table from 'react-bootstrap/Table'
import Header from '../../components/Header/Header'
import axios from 'axios'

const HeaderProps = {
    title: "Listagem de pessoas"
}

const GetUsers = {}

const Users = []



class UserView extends Component {



    async componentDidMount() {
        axios.get('http://localhost:3333/usuarios')
            .then(function (res) {
                // response.map(item => [console.log(item.id)])
                GetUsers.data = res.data
                // console.log(GetUsers.data[0].id)   
                const TamanhoObjetoUsuarios = Object.values(GetUsers)[0].length
                for (let index = 0; index < TamanhoObjetoUsuarios; index++) {
                    const name = Object.values(GetUsers)[0][index].name
                    const id = Object.values(GetUsers)[0][index].id
                    const email = Object.values(GetUsers)[0][index].email
                    Users.push({name, id, email})
                }
            })
            console.log(
                Users.map((value, Index) => {
                    console.log
           })
            )
    }

    render() {
        return (
            <div>
                <div>
                    <Header  {...HeaderProps} />
                </div>
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Idade</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                        
                        </tr>
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default UserView;
