import React, { Component } from 'react'
import './UserView.css'

import Header from '../../components/Header/Header'

import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'
import { faWrench, faTrash, faUserPlus } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const HeaderProps = {
    title: "Listagem de pessoas"
}

const icons = {
    Wrench: <FontAwesomeIcon icon={faWrench}/>,
    Trash: <FontAwesomeIcon icon={faTrash}/>,
    UserPlus: ` Adiconar ${<FontAwesomeIcon icon={faUserPlus}/>}`,
}


const buttons = {
    success: function(params) {
        return <Button variant="success">{params}</Button>
    },
    add: function(params) {
        return <Button variant="success">{params}</Button>
    },
    danger: function(params) {
        return <Button variant="danger">{params}</Button>
    },
}

class UserView extends Component {

    constructor(props) {
        super(props)
        this.state = {
            users: []
        }
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {

        fetch('http://localhost:3333/usuarios')
            .then(res => res.json())
            .then(res => this.setState({ users: res}))
    }

    handleClick(id) {
        fetch(`http://localhost:3333/deleteUser/${id}`,{
            method: 'DELETE'
        })
            .then(res => res.json())
            .then(res => this.setState({ users: res}))
    }

    render() {
        const { users } = this.state
        const handleClick = this.handleClick
        return (
            <div>
                {console.log(this.state.users)}
                <div>
                    <Header  {...HeaderProps} />
                </div>
                <Table striped bordered hover responsive className="table-userview">
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(function (value, index) {
                            return (
                                <tr key={index}>
                                    <td>{value.name}</td>
                                    <td>{value.email}</td>
                                    <td> <a href={``}>{buttons.success(icons.Wrench)}</a> <Button onClick={e => handleClick(value.id)} variant="danger">{icons.Wrench}</Button></td>
                                </tr>
                            )
                        })}
                    </tbody>
                </Table>
                <div>
                    <a href="/create" className="button-add-userview" >{buttons.add("Adicionar pessoa")}</a>
                </div>
            </div>
        )
    }
}

export default UserView;
