import React, { Component } from 'react'
import './UserView.jsx'

import Table from 'react-bootstrap/Table'
import Header from '../../components/Header/Header'
import axios from 'axios'

const HeaderProps = {
    title: "Listagem de pessoas"
}

class UserView extends Component {

    constructor(props) {
        super(props)
        this.state = {
            users: []
        }
    }

    componentDidMount() {
        // axios.get('http://localhost:3333/usuarios')
        //     .then(function (res) {
        //         // this.setState({ users: res.data })
        //     })

        fetch('http://localhost:3333/usuarios')
            .then(res => {
                res.json()
            })
            .then(json => {
                console.log('parsed json', json.lis)
            })
    }

    render() {
        return (
            <div>
                <div>
                    <Header  {...HeaderProps} />
                </div>
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Idade</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.users.map(function (value, index) {
                            return (
                                <tr>
                                    <td>{value.name}</td>
                                </tr>
                            )
                        })}
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default UserView;
