import React, { Component } from 'react'
import './UserCreate.css'
import Header from '../../components/Header/Header'

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
// import InputGroup from 'react-bootstrap/InputGroup'
import { Col  } from 'react-bootstrap';

const HeaderProps = {
    title: "Cadastrar pessoa"
}



class UserCreate extends Component {

    constructor(){
        super()
        this.state = {
            Firstname: '',
            Lastname: '',
            email: ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({Firstname: event.target.Firstname, Lastname: event.target.Firstname });
      }

    handleSubmit(event) {
        const name = event.target.Firstname + ' ' + event.target.Lastname
        alert('Um nome foi enviado: ' + name);
        event.preventDefault();
      }

    render() {
        return (
            <div>
                <Header {...HeaderProps} />
                <Form className="form-userCreate" onSubmit={this.handleSubmit}>
                    <Form.Row>
                        <Form.Group as={Col} md="4" controlId="validationCustom01">
                            <Form.Label>First name</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                placeholder="First name"
                                defaultValue=""
                                value={this.state.Firstname}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group as={Col} md="4" controlId="validationCustom02">
                            <Form.Label>Last name</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                placeholder="Last name"
                                defaultValue=""
                                value={this.state.Lastname}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                    <Form.Group controlId="formBasicEmail">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control type="email" placeholder="Enter email" />
                            <Form.Text className="text-muted">
                                We'll never share your email with anyone else.
                            </Form.Text>
                        </Form.Group>
                    </Form.Row>
                    <Button className="button-submit-UserCreate" variant="primary">Confirm</Button>
                    <Button className="button-cancel-UserCreate" variant="danger"><a href="/">Cancel</a></Button>
                </Form>
            </div>
        )
    }
}

export default UserCreate;
