import React, { Component } from 'react'
import './UserView.jsx'

import Table from 'react-bootstrap/Table'
import Header from '../../components/Header/Header'

const HeaderProps = {
    title: "Listagem de pessoas"
}

const abc = [{
    name: "wagner",
    age: 23
}, {
    name: "Alves",
    age: 23
}
]

const FormTable = (array) => {

    const OpenTable = `<ul>`
    const ClosedTable = `</ul>`
    let ContentTable = ``

    for (let i = 0; i < array.length; i++) {
        ContentTable += `
            <li>Nome: ${array[i].name}</li>
            <li>Idade: ${array[i].age}</li>
        `
    }

    return OpenTable + ContentTable + ClosedTable

}

class UserView extends Component {
    render() {
        return (
            <div>
                <Header {...HeaderProps} />
                <Table responsive>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Idade</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>1</td>
                            {abc.map((item, index) => (
                                <td key={index}>Table cell {index}</td>
                            ))}
                        </tr>
                        <tr>
                            <td>2</td>
                            {abc.map((item, index) => (
                                <td key={index}>{item.name}</td>
                            ))}
                        </tr>
                        <tr>
                            <td>3</td>
                            {abc.map((item, index) => (
                                <td key={index}>{item.age}</td>
                            ))}
                        </tr>
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default UserView;
