import React, { Component } from 'react'
import './UserCreate.css'
import Header from '../../components/Header/Header'

import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
// import InputGroup from 'react-bootstrap/InputGroup'
import { Col  } from 'react-bootstrap';

const HeaderProps = {
    title: "Cadastrar pessoa"
}



class UserCreate extends Component {

    constructor(){
        super()
        this.state = {
            name: 'oko',
            email: ''
        }
    }

    handleChange(event) {
        this.setState({email: event.target.email});
      }

    handleSubmit(event) {
        alert('Um nome foi enviado: ' + this.state.name);
        event.preventDefault();
      }

    render() {
        return (
            <div>
                <Header {...HeaderProps} />
                <Form className="form-userCreate" onSubmit={this.handleSubmit}>
                    <Form.Row>
                        <Form.Group as={Col} md="4" controlId="validationCustom01">
                            <Form.Label>First name</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                placeholder="First name"
                                onChange={this.handleChange}
                                value={this.state.name}
                            />
                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                    <Form.Group controlId="formBasicEmail">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control type="email" placeholder="Enter email" />
                            <Form.Text 
                                className="text-muted"
                                onChange={this.handleChange}
                                value={this.state.email}>
                                We'll never share your email with anyone else.
                            </Form.Text>
                            <p>email: {this.state.email} </p>
                        </Form.Group>
                    </Form.Row>
                    <Button className="button-submit-UserCreate" variant="primary">Confirm</Button>
                    <Button className="button-cancel-UserCreate" variant="danger"><a href="/">Cancel</a></Button>
                </Form>
            </div>
        )
    }
}

export default UserCreate;
