import React, { Component } from 'react'
import './UserView.css'

import Header from '../../components/Header/Header'

import Table from 'react-bootstrap/Table'
import Button from 'react-bootstrap/Button'
import { faWrench, faTrash, faUserPlus } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const HeaderProps = {
    title: "Listagem de pessoas"
}

const icons = {
    Wrench: <FontAwesomeIcon icon={faWrench}/>,
    Trash: <FontAwesomeIcon icon={faTrash}/>,
    UserPlus: <FontAwesomeIcon icon={faUserPlus}/>,
}


const buttons = {
    success: function(params) {
        return <Button variant="success">{params}</Button>
    },
    danger: function(params) {
        return <Button variant="danger">{params}</Button>
    }
}

class UserView extends Component {

    constructor(props) {
        super(props)
        this.state = {
            users: []
        }
    }

    componentDidMount() {

        fetch('http://localhost:3333/usuarios')
            .then(res => res.json())
            .then(res => this.setState({ users: res}))
    }

    render() {
        const { users } = this.state
        return (
            <div>
                {console.log(this.state.users)}
                <div>
                    <Header  {...HeaderProps} />
                </div>
                <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(function (value, index) {
                            return (
                                <tr key={index}>
                                    <td>{value.name}</td>
                                    <td>{value.email}</td>
                                    <td> <a href="/update">{buttons.success(icons.Wrench)}</a> {buttons.danger(icons.Trash)} </td>
                                </tr>
                            )
                        })}
                    </tbody>
                </Table>
                <div>
                    <a href="/update">{`Adidionar buttons.success(icons.UserPlus)` }</a>
                </div>
            </div>
        )
    }
}

export default UserView;
