import React, { Component } from 'react'
import './UserView.jsx'

import Table from 'react-bootstrap/Table'
import Header from '../../components/Header/Header'

const HeaderProps = {
    title: "Listagem de pessoas"
}

const abc = [{
    name: "wagner",
    age: 23
}, {
    name: "Alves",
    age: 23
}
]


class UserView extends Component {
    render() {
        return (
            <div>
                <Header {...HeaderProps} />
                <Table responsive>
                    <thead>
                        <tr><Header {...HeaderProps} /></tr>
                        <tr>
                            <th>Nome</th>
                            <th>Idade</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            {abc.map((item, index) => (
                                <td key={index}>{item.name}</td>
                            ))}
                        </tr>
                        <tr>
                            {abc.map((item, index) => (
                                <td key={index}>{item.age}</td>
                            ))}
                        </tr>
                    </tbody>
                </Table>
            </div>
        )
    }
}

export default UserView;
